mapper.select:
SELECT no, mountain_no, rname, rloc, contact, menu, description, likecnt, dislikecnt, mname
FROM
(SELECT ROW_NUMBER() OVER (ORDER BY no DESC) rn, no, mountain_no, rname, rloc, contact, menu, description,
likecnt, dislikecnt, mname FROM res_view




)
WHERE rn BETWEEN (#{pageNo} -1) * #{amount} + 1 AND #{pageNo} * #{amount}

SELECT no, mountain_no, rname, rloc, contact, menu, description, likecnt, dislikecnt, mname FROM
(SELECT ROW_NUMBER() OVER (ORDER BY no DESC) rn, no, mountain_no, rname, rloc, contact, menu, description, likecnt, dislikecnt, mname FROM res_view)
WHERE no = #{no}
